import React, { useRef, useState } from 'react';
import './App.css';
import Pane from './Pane'; // Import your Pane component

const App = () => {
  const [currentPage, setCurrentPage] = useState(0);
  const appRef = useRef(null);

  const handleScroll = () => {
    if (appRef.current) {
      const pageNumber = Math.round(appRef.current.scrollLeft / window.innerWidth);
      setCurrentPage(pageNumber);
    }
  };

  const scrollToPage = (page) => {
    if (appRef.current) {
      appRef.current.scrollTo({
        left: window.innerWidth * page,
        behavior: 'smooth',
      });
      setCurrentPage(page);
    }
  };

  return (
    <div className="App">
      <div className="scroll-container" ref={appRef}>
        <Pane title="Forest" backgroundColor="#9EEBCF" />
        <Pane title="Beach" backgroundColor="#F5A494" />
        <Pane title="Countryside" backgroundColor="#FFD56D" />
        <Pane title="Urban" backgroundColor="#9A77B8" />
        <Pane title="Park" backgroundColor="#AAD8B0" />
      </div>

      {/* Page Indicator */}
      <div className="page-indicator">
        {[0, 1, 2, 3, 4].map((index) => (
          <span
            key={index}
            className={index === currentPage ? 'active' : ''}
            onClick={() => scrollToPage(index)}
          ></span>
        ))}
      </div>

      {/* Navigation Buttons */}
      <button onClick={() => scrollToPage(currentPage - 1)} disabled={currentPage === 0}>{'<'}</button>
      <button onClick={() => scrollToPage(currentPage + 1)} disabled={currentPage === 4}>{'>'}</button>
    </div>
  );
};

export default App;
