import React, { useState } from 'react';

const Pane = ({ title, backgroundColor }) => {
  const paneStyle = {
    backgroundColor,
    width: '100vw',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'center',
    scrollSnapAlign: 'start',
    flexShrink: 0,
    padding: '20px', // Adjust padding as needed
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: 0,
    marginBottom: '10px', // Adjust margin as needed
  };

  const tileContainerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: '20px', // Adjust the gap between tiles
  };

  const tileStyle = {
    width: '100px',
    height: '100px',
    borderRadius: '50%',
    backgroundColor: 'lightgray',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    cursor: 'pointer',
    border: '3px solid transparent',
  };

  const activeTileStyle = {
    border: '3px solid green',
  };

  // ... (rest of the code remains the same)

  return (
    <div style={paneStyle}>
      <h2 style={titleStyle}>{title}</h2>
      <div style={tileContainerStyle}>
        {tiles[title].map((sound, index) => (
          <div key={index}>
            <div
              style={{ ...tileStyle, ...(activeSounds[sound] ? activeTileStyle : {}) }}
              onClick={() => playSound(sound)}
            >
              {sound}
            </div>
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={volume[sound] || 0.5}
              style={volumeSliderStyle}
              onChange={(event) => handleVolumeChange(event, sound)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Pane;
