import React, { useState } from 'react';

const SoundTile = ({ sound, isActive, onClick }) => {
  const ringColor = isActive ? 'green' : 'transparent'; // Change ring color based on isActive status

  return (
    <div
      onClick={onClick}
      style={{
        position: 'relative',
        width: '100px',
        height: '100px',
        margin: '10px',
        cursor: 'pointer',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {/* Translucent ring */}
      <svg width="100%" height="100%" viewBox="0 0 100 100" style={{ position: 'absolute' }}>
        <circle
          cx="50%"
          cy="50%"
          r="45%"
          stroke={ringColor}
          strokeWidth="8"
          fill="none"
          strokeLinecap="round"
        />
      </svg>

      {/* Icon */}
      <img
        src={`/icons/${sound}.svg`} // Replace with your icon path
        alt={sound}
        style={{
          width: '60%',
          height: '60%',
          zIndex: '1',
        }}
      />
      {/* Sound name */}
      <p style={{ margin: '5px 0 0', fontSize: '14px' }}>{sound}</p>
    </div>
  );
};

const Pane = ({ title }) => {
  const [activeSounds, setActiveSounds] = useState({});
  const [volume, setVolume] = useState({});

  const playSound = (sound) => {
    let audio = new Audio(`/sounds/${sound}.mp3`);
    audio.loop = true;
    audio.volume = volume[sound] || 0.5;

    if (!activeSounds[sound]) {
      audio.play();
      setActiveSounds({ ...activeSounds, [sound]: audio });
    } else {
      audio = activeSounds[sound];
      if (audio.paused) {
        audio.play();
      } else {
        audio.pause();
        audio.currentTime = 0;
        setActiveSounds((prevActiveSounds) => ({
          ...prevActiveSounds,
          [sound]: null,
        }));
      }
    }
  };

  const handleVolumeChange = (event, sound) => {
    const newVolume = event.target.value;
    setVolume({ ...volume, [sound]: newVolume });
    if (activeSounds[sound]) {
      activeSounds[sound].volume = newVolume;
    }
  };

  const tiles = {
    Forest: ['wind', 'crickets', 'river', 'frogs', 'rain'],
    Beach: ['waves', 'seagulls', 'storm', 'boat', 'rain'],
    Countryside: ['hens', 'stream', 'wind', 'rain', 'birds'],
    Park: ['fountain', 'kids', 'birds', 'duck', 'dog'],
    Urban: ['traffic', 'people', 'wind', 'rain', 'police'],
  };

  return (
    <div
      style={{
        width: '100vw',
        height: '100vh',
        backgroundImage: `url(/images/${title.toLowerCase()}.jpg)`, // Set background image dynamically
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
        color: 'white',
        textAlign: 'center',
        overflowY: 'scroll', // Enable vertical scrolling
      }}
    >
      <h2 style={{ fontSize: '24px', fontWeight: 'bold', margin: '0', padding: '20px' }}>{title}</h2>
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'flex-start',
          padding: '20px',
        }}
      >
        {tiles[title].map((sound, index) => (
          <SoundTile
            key={index}
            sound={sound}
            isActive={!!activeSounds[sound]}
            onClick={() => playSound(sound)}
          />
        ))}
        {tiles[title].map((sound, index) => (
          <input
            key={`input-${index}`}
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={volume[sound] || 0.5}
            onChange={(event) => handleVolumeChange(event, sound)}
          />
        ))}
      </div>
    </div>
  );
};

export default Pane;
