import React, { useState } from 'react';

const Pane = ({ title, backgroundColor }) => {
  const paneStyle = {
    backgroundColor,
    width: '100vw',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    scrollSnapAlign: 'start',
    flexShrink: 0,
    padding: '20px', // Adjust padding as needed
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: 0,
    marginBottom: '10px', // Adjust margin as needed
  };

  const tileContainerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
  };

  const tileStyle = {
    width: '100px',
    height: '100px',
    borderRadius: '50%',
    backgroundColor: 'lightgray',
    margin: '10px',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    cursor: 'pointer',
    border: '3px solid transparent',
  };

  const activeTileStyle = {
    border: '3px solid green',
  };

  const tiles = {
    Forest: ['wind', 'crickets', 'river', 'frogs', 'rain'],
    Beach: ['waves', 'seagulls', 'storm', 'boat', 'rain'],
    Countryside: ['hens', 'stream', 'wind', 'rain', 'birds'],
    Park: ['fountain', 'kids', 'birds', 'duck', 'dog'],
    Urban: ['traffic', 'people', 'wind', 'rain', 'police'],
  };

  const [activeSounds, setActiveSounds] = useState({});

  const playSound = (sound) => {
    const audio = new Audio(`/sounds/${sound}.mp3`);

    if (!activeSounds[sound]) {
      audio.play();
      setActiveSounds({ ...activeSounds, [sound]: true });
    } else {
      audio.pause();
      audio.currentTime = 0;
      setActiveSounds({ ...activeSounds, [sound]: false });
    }
  };

  return (
    <div style={paneStyle}>
      <h2 style={titleStyle}>{title}</h2>
      <div style={tileContainerStyle}>
        {tiles[title].map((sound, index) => (
          <div
            key={index}
            style={{ ...tileStyle, ...(activeSounds[sound] && activeTileStyle) }}
            onClick={() => playSound(sound)}
          >
            {sound}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Pane;
