import React from 'react';

const Pane = ({ title, backgroundColor }) => {
  const paneStyle = {
    backgroundColor,
    width: '100vw',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    scrollSnapAlign: 'start',
    flexShrink: 0,
    padding: '20px', // Adjust padding as needed
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: 0,
    marginBottom: '10px', // Adjust margin as needed
  };

  return (
    <div style={paneStyle}>
      <h2 style={titleStyle}>{title}</h2>
      <div> {/* Your pane content goes here */}</div>
    </div>
  );
};

export default Pane;
