import React from 'react';

const Pane = ({ title, backgroundColor }) => {
  const paneStyle = {
    backgroundColor,
    width: '100vw',
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    scrollSnapAlign: 'start',
    flexShrink: 0,
  };

  return (
    <div style={paneStyle}>
      <h2>{title}</h2>
    </div>
  );
};

export default Pane;
