import React, { useState } from 'react';

const Pane = ({ title, backgroundColor }) => {
  const paneStyle = {
    backgroundColor,
    width: '100vw',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    scrollSnapAlign: 'start',
    flexShrink: 0,
    padding: '20px', // Adjust padding as needed
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    margin: 0,
    marginBottom: '10px', // Adjust margin as needed
  };

  const tileContainerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
  };

  const tileStyle = {
    width: '100px',
    height: '100px',
    borderRadius: '50%',
    backgroundColor: 'lightgray',
    margin: '10px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    cursor: 'pointer',
    border: '3px solid transparent',
  };

  const activeTileStyle = {
    border: '3px solid green',
  };

  const volumeSliderStyle = {
    width: '80%',
    marginTop: '10px',
  };

  const tiles = {
    Forest: ['wind', 'crickets', 'river', 'frogs', 'rain'],
    Beach: ['waves', 'seagulls', 'storm', 'boat', 'rain'],
    Countryside: ['hens', 'stream', 'wind', 'rain', 'birds'],
    Park: ['fountain', 'kids', 'birds', 'duck', 'dog'],
    Urban: ['traffic', 'people', 'wind', 'rain', 'police'],
  };

  const [activeSounds, setActiveSounds] = useState({});
  const [volume, setVolume] = useState(0.5);

  const playSound = (sound) => {
    let audio = new Audio(`/sounds/${sound}.mp3`);
    audio.loop = true;
    audio.volume = volume;

    if (!activeSounds[sound]) {
      audio.play();
      setActiveSounds({ ...activeSounds, [sound]: audio });
    } else {
      audio = activeSounds[sound];
      if (audio.paused) {
        audio.play();
      } else {
        audio.pause();
        audio.currentTime = 0;
        setActiveSounds({ ...activeSounds, [sound]: null });
      }
    }
  };

  const handleVolumeChange = (event, sound) => {
    const newVolume = event.target.value;
    setVolume(newVolume);
    if (activeSounds[sound]) {
      activeSounds[sound].volume = newVolume;
    }
  };

  return (
    <div style={paneStyle}>
      <h2 style={titleStyle}>{title}</h2>
      <div style={tileContainerStyle}>
        {tiles[title].map((sound, index) => (
          <div key={index}>
            <div
              style={{ ...tileStyle, ...(activeSounds[sound] ? activeTileStyle : {}) }}
              onClick={() => playSound(sound)}
            >
              {sound}
            </div>
            <input
              type="range"
              min="0"
              max="1"
              step="0.1"
              value={volume}
              style={volumeSliderStyle}
              onChange={(event) => handleVolumeChange(event, sound)}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Pane;
